const addCartButtons=document.querySelectorAll(".option-buy"),cartPopup=document.querySelector(".modal-cart"),cartClose=cartPopup.querySelector(".modal-close"),buttonContinue=cartPopup.querySelector(".button-continue");for(let a=0;a<addCartButtons.length;a++)addCartButtons[a].addEventListener("click",function(a){a.preventDefault(),cartPopup.classList.add("modal-show")});cartClose.addEventListener("click",function(a){a.preventDefault(),cartPopup.classList.remove("modal-show")}),buttonContinue.addEventListener("click",function(a){a.preventDefault(),cartPopup.classList.remove("modal-show")}),window.addEventListener("keydown",function(a){"Escape"===a.key&&cartPopup.classList.contains("modal-show")&&(a.preventDefault(),cartPopup.classList.remove("modal-show"))});
